import { extend, ChoiceList, Choice, BlockStack, InlineStack } from '@shopify/checkout-ui-extensions';
extend('Checkout::Dynamic::Render', function (root) {
  var choiceList = root.createComponent(ChoiceList, {
    name: 'choice',
    value: 'first',
    onChange: function onChange(value) {
      console.log("onChange event with value: ".concat(value));
    }
  }, [root.createComponent(BlockStack, undefined, [root.createComponent(Choice, {
    id: 'first'
  }, 'Ship'), root.createComponent(Choice, {
    id: 'second'
  }, 'Pickup')])]);
  var multipleChoiceList = root.createComponent(ChoiceList, {
    name: 'multipleChoice',
    value: ['multipleFirst'],
    onChange: function onChange(value) {
      console.log("onChange event with value: ".concat(value));
    }
  }, [root.createComponent(BlockStack, undefined, [root.createComponent(Choice, {
    id: 'multipleFirst'
  }, 'Gift message'), root.createComponent(Choice, {
    id: 'multipleSecond'
  }, 'Gift wrapping')])]);
  var layout = root.createComponent(InlineStack, undefined, [choiceList, multipleChoiceList]);
  root.appendChild(layout);
});