import { extend, BlockSpacer, Button, Form, Grid, GridItem, TextField, View } from '@shopify/checkout-ui-extensions';
extend('Checkout::Dynamic::Render', function (root) {
  var fields = root.createComponent(Grid, {
    columns: ['50%', '50%'],
    spacing: 'base'
  }, [root.createComponent(View, undefined, root.createComponent(TextField, {
    label: 'First name'
  })), root.createComponent(View, undefined, root.createComponent(TextField, {
    label: 'Last name'
  })), root.createComponent(GridItem, {
    columnSpan: 2
  }, root.createComponent(TextField, {
    label: 'Company'
  }))]);
  var spacer = root.createComponent(BlockSpacer, {
    spacing: 'base'
  });
  var button = root.createComponent(Button, {
    submit: true
  }, 'Submit');
  var form = root.createComponent(Form, {
    onSubmit: function onSubmit() {
      return console.log('onSubmit event');
    }
  }, [fields, spacer, button]);
  root.appendChild(form);
});